svg.script:

var href = document.defaultView.location.href,
    color = null;

if ( -1 != href.indexOf("?") ) {
  var param_list = href.split("?")[1].split(/&|;/),
      param_count = param_list.length;

  for (var p = 0; param_count > p; p++) {
    var c_param = param_list[p],
        key_arr = c_param.split('='),
        name = key_arr[0],
        value = key_arr[1];

    if(name === 'color' && value)
      color = value;
  }
}

var logo = document.getElementById("filler");
logo.setAttribute("fill", color);
